export class NodeApiDataSource {
    client;
    constructor(client) {
        this.client = client;
    }
    async refreshToken(refreshToken, rpcBaseUrl) {
        return await this.client.post(`${rpcBaseUrl}/admin-api/refresh-jwt-token`, {
            refreshToken,
        });
    }
    async health(request) {
        return await this.client.get(`${request.url}/admin-api/health`);
    }
}
